import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({
  contacts,
  name,
  setTitle,
  contact,
  setContact,
  date,
  setDate,
  time,
  setTime,
  handleSubmit
}) => {

  return (
    <>
    <form onSubmit={handleSubmit}>
      <label for="name">Name:</label>
      <input required id="name" type="text" value={name} onChange={(e) => setTitle(e.target.value)}/>
      <label for="date">Date:</label>
      <input required id="date" type="date" min={getTodayString()} value={date} onChange={(e) => setDate(e.target.value)}/>
      <label for="time">Time</label>
      <input required id="time" type="time" value={time} onChange={(e) => setTime(e.target.value)}/>
      <input type="submit"/>
    </form>
    </>
  );
};
